import { createContext, useReducer, useEffect, useState, useContext } from "react";
import { AuthContext } from "./AuthContext";

export const CartContext = createContext();

const initialState = {
  cartItems: []
};

const cartReducer = (state, action) => {
  switch (action.type) {
    case "SET_CART":
      return { ...state, cartItems: action.payload };

    case "ADD_ITEM":
      const exist = state.cartItems.find(item => item.id === action.payload.id);
      if (exist) {
        return {
          ...state,
          cartItems: state.cartItems.map(item =>
            item.id === action.payload.id
              ? { ...item, quantity: item.quantity + action.payload.quantity }
              : item
          )
        };
      } else {
        return {
          ...state,
          cartItems: [...state.cartItems, action.payload]
        };
      }

    case "UPDATE_ITEM": // 이미 존재하는 상품에 대해서만 수량 update
      return {
        ...state,
        cartItems: state.cartItems.map(item =>
          item.id === action.payload.id
            ? { ...item, quantity: action.payload.quantity }
            : item
        )
      };

    case "REMOVE_ITEM":
      return {
        ...state,
        cartItems: state.cartItems.filter(item => item.id !== action.payload)
      };

    case "CLEAR_CART":
      return { ...state, cartItems: [] };

    default:
      return state;
  }
};

export const CartProvider = ({ children }) => {
  const [cartState, dispatch] = useReducer(cartReducer, initialState);
  const { userInfo } = useContext(AuthContext);
  const [initialized, setInitialized] = useState(false);


  // 최초 렌더링 시 localStorage에서 불러오기 (비회원)
  useEffect(() => {
    if (!userInfo.token) {
      const localItems = JSON.parse(localStorage.getItem("cartItems")) || [];
      dispatch({ type: "SET_CART", payload: localItems });
    }
    setInitialized(true);
  }, [userInfo.token]);

  // 비회원일 때는 localStorage에 저장
  useEffect(() => {
    if(!userInfo.token && initialized) {
      localStorage.setItem("cartItems", JSON.stringify(cartState.cartItems));
    }
  }, [cartState.cartItems, userInfo.token, initialized]);


  // 로그인 시 서버 장바구니 병합
  useEffect(() => {
    const mergeCart = async () => {
      const localItems = JSON.parse(localStorage.getItem("cartItems")) || [];
    /*
      try {
        // 서버 장바구니 가져오기
        const res = await fetch("/api/cart", {
          headers: { Authorization: `Bearer ${userInfo.token}` }
        });
        const serverItems = await res.json(); // [{ id, quantity }, ...]

        // 병합 로직
        const merged = [...serverItems];
        localItems.forEach(localItem => {
          const existing = merged.find(item => item.id === localItem.id);
          if (existing) {
            existing.quantity += localItem.quantity;
          } else {
            merged.push(localItem);
          }
        });

        // 병합된 장바구니 서버에 저장
        await fetch("/api/cart/merge", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${userInfo.token}`
          },
          body: JSON.stringify(merged)
        });

        // context state 반영
        dispatch({ type: "SET_CART", payload: merged });

        // localStorage 초기화
        localStorage.removeItem("cartItems");
      } catch (err) {
        console.error("장바구니 병합 실패:", err);
      }
      */
    };
    if (userInfo.token) {
      mergeCart();
    }
  }, [userInfo.token]);

  return (
    <CartContext.Provider value={{ cartState, dispatch }}>
      {children}
    </CartContext.Provider>
  );
};
