import { Link } from "react-router-dom";

function Sidebar() {
  return (
    <div className="w-48 bg-white shadow h-screen p-4 space-y-2">
      <h2 className="text-gray-600 font-semibold">목록</h2>

      <div className="group relative">
        <button className="block w-full text-left px-2 py-1 hover:bg-gray-100 rounded">회원관리</button>

        {/* 드롭다운 */}
        <div className="absolute left-full top-0 ml-1 hidden group-hover:block bg-white shadow-md rounded w-40 z-999">
          <Link to="/userInfo" className="w-full text-left px-4 py-2 hover:bg-gray-100">회원정보</Link>
          <button className="w-full text-left px-4 py-2 hover:bg-gray-100">포인트관리</button>
        </div>
      </div>

      <button className="block w-full text-left px-2 py-1 hover:bg-gray-100 rounded">
        쇼핑몰관리
      </button>
      <button className="block w-full text-left px-2 py-1 hover:bg-gray-100 rounded">
        쇼핑몰현황
      </button>
    </div>
  );
}

export default Sidebar;
