import { useEffect, useState } from "react";
import api from "../../../api/apiClient";
import { Link } from "react-router-dom";

function OrderHistory() {
  const [orders, setOrders] = useState([]);
  const [page, setPage] = useState(0);
  const [size, setSize] = useState(10);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    api.get(`/admin/orders?page=${page}&size=${size}`)
      .then(response => {
        setOrders(response.data.content);
        setTotalPages(response.data.totalPages);
      })
      .catch(error => {
        console.error("주문 내역을 불러오지 못했습니다.", error);
      });
  }, [page, size]);

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <div className="text-2xl font-bold">주문내역</div>
      </div>
주문번호	상품정보	수량	금액	배송상태
      <div className="overflow-x-auto">
        <table className="min-w-full border border-gray-200">
          <thead className="bg-gray-100 text-center">
            <tr>
              <th className="px-4 py-2 border">주문번호</th>
              <th className="px-4 py-2 border">주문자 이메일</th>
              <th className="px-4 py-2 border">주문일자</th>
              <th className="px-4 py-2 border">상품정보</th>
              <th className="px-4 py-2 border">수량</th>
              <th className="px-4 py-2 border">총 금액</th>
              <th className="px-4 py-2 border">배송상태</th>
            </tr>
          </thead>
          <tbody>
            {orders.length > 0 ? (
              orders.map((order) => (
                <tr key={order.orderId} className="text-center">
                  <td className="px-4 py-2 border">{order.orderId}</td>
                  <td className="px-4 py-2 border">{order.userEmail}</td>
                  <td className="px-4 py-2 border">{new Date(order.orderDate).toLocaleDateString()}</td>
                  <td className="px-4 py-2 border text-blue-600 font-semibold">{order.totalPrice.toLocaleString()}원</td>
                  <td className="px-4 py-2 border">{order.status === "PAID" ? "결제완료" : order.status}</td>
                  <td className="px-4 py-2 border">
                    <Link
                      to={`/admin/order-detail/${order.orderId}`}
                      className="inline-flex items-center justify-center bg-blue-500 text-white px-4 py-2 rounded"
                    >
                      상세
                    </Link>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="text-center py-8 text-gray-500">
                  주문 내역이 없습니다.
                </td>
              </tr>
            )}
          </tbody>
        </table>

        <div className="flex justify-end items-center gap-4 mt-4 text-sm text-gray-700">
          <div className="flex items-center gap-2">
            <span>Rows per page:</span>
            <select
              value={size}
              onChange={(e) => setSize(Number(e.target.value))}
              className="border rounded px-2 py-1 bg-white"
            >
              <option value={10}>10</option>
              <option value={20}>20</option>
              <option value={30}>30</option>
            </select>
          </div>

          <div className="flex items-center gap-2">
            <button
              onClick={() => setPage(page - 1)}
              disabled={page === 0}
              className={`px-2 py-1 rounded ${
                page === 0 ? 'text-gray-400 cursor-not-allowed' : 'text-blue-600 hover:underline'
              }`}
            >
              &lt;
            </button>

            <span className="min-w-[80px] text-center">Page {page + 1} of {totalPages}</span>

            <button
              onClick={() => setPage(page + 1)}
              disabled={page >= totalPages - 1}
              className={`px-2 py-1 rounded ${
                page >= totalPages - 1 ? 'text-gray-400 cursor-not-allowed' : 'text-blue-600 hover:underline'
              }`}
            >
              &gt;
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default OrderHistory;
