import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../api/apiClient";

function ProfileEdit() {
    const navigate = useNavigate();

    const [userData, setUserData] = useState({
        email: "",
        name: "",
        birth: "",
        phone: "",
        address: "",
    });

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const res = await api.get("/user/profile");
                if (res.status === 200) setUserData(res.data);
            } catch {
                alert("회원 정보를 불러오는 중 오류가 발생했습니다.");
            }
        };
        fetchProfile();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validate()) return;

        try {
            const res = await api.put("/user/profile", userData);
            if (res.status === 200) {
                alert("회원 정보가 수정되었습니다.");
                navigate("/mypage");
            }
        } catch {
            alert("회원 정보 수정에 실패했습니다.");
        }
    };

    const validate = () => {
        const { email, name, birth, phone, address } = userData;
        if (!email.includes("@")) return alert("올바른 이메일을 입력해주세요.");
        if (!/^\d{6}$/.test(birth)) return alert("생년월일은 6자리 숫자입니다.");
        if (!phone) return alert("전화번호를 입력해주세요.");
        if (!address) return alert("주소를 입력해주세요.");
        if (!name) return alert("이름을 입력해주세요.");
        return true;
    };

    return (
        <div className="w-full max-w-2xl mx-auto px-6 py-16">
            <h2 className="text-2xl font-bold text-black mb-10 border-b pb-4">
                회원 정보 수정
            </h2>

            <form onSubmit={handleSubmit} className="space-y-8">
                {[
                    { label: "이메일", name: "email" },
                    { label: "이름", name: "name" },
                    { label: "생년월일", name: "birth", maxLength: 6 },
                    { label: "전화번호", name: "phone", maxLength: 11 },
                    { label: "주소", name: "address" },
                ].map(({ label, name, maxLength }) => (
                    <div key={name}>
                        <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
                        <input
                            name={name}
                            value={userData[name]}
                            maxLength={maxLength}
                            className="w-full border-b border-gray-300 focus:outline-none focus:border-black text-black"
                            onChange={(e) => {
                                const value = name === "birth" || name === "phone"
                                    ? e.target.value.replace(/[^0-9]/g, "")
                                    : e.target.value;
                                setUserData((prev) => ({ ...prev, [name]: value }));
                            }}
                        />
                    </div>
                ))}

                <div className="flex justify-center mt-10">
                    <button
                        type="submit"
                        className="px-6 py-3 bg-black text-white hover:bg-gray-900 transition rounded-md"
                    >
                        정보 수정 완료
                    </button>
                </div>
            </form>
        </div>
    );
}

export default ProfileEdit;
