import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Header({ categories }) {
  
  const [activeTab, setActiveTab] = useState(null);
  
  useEffect(() => {
    if(categories.length > 0){
      setActiveTab(categories[2].id);
    }
    console.log(categories);
  }, [categories]);

  const renderedCategories = [
    ...categories,
    { id: 999, name: '관리자 페이지', isAdminLink: true }
  ];

  return (
    <div>
      {/* Header 영역 */}
      <header className="bg-gray-100 shadow py-10 px-4 flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <button className="border px-4 py-1 rounded">로그인</button>
          <button className="border px-4 py-1 rounded">회원가입</button>
        </div>
        <h1 className="text-3xl font-bold">ShoppingMall</h1>
        <div className="w-24" />
      </header>

      {/* 카테고리 네비게이션 바 (Header 아래) */}
      <nav className="bg-white border-b w-full shadow-sm">
        <ul className="flex justify-center space-x-24 px-6 overflow-x-auto">
          {renderedCategories.map((category) =>
            category.isAdminLink ? (
              <li key={category.id}>
                <Link
                  to="/admin"
                  className="block py-3 px-4 text-sm font-medium text-gray-800 bg-gray-200 hover:bg-gray-300"
                >
                  {category.name}
                </Link>
              </li>
            ) : (
              <li
                key={category.id}
                className={`cursor-pointer py-3 px-2 text-sm font-medium border-b-2 transition-colors ${
                  activeTab === category.id
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-600 hover:text-blue-500'
                }`}
                onClick={() => setActiveTab(category.id)}
              >
                {category.name}
              </li>
            )
          )}
        </ul>
      </nav>
    </div>
  );
}

export default Header;
