import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

function ProductRegistration() {
  const [productData, setProductData] = useState({
    productCode: "",
    productName: "",
    productDesc: "",
    price: "",
    categoryName: "",
    stock: "",
    image: null,
  });

  const [categories, setCategories] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/product/getAllCategories")
    .then(response => {
        setCategories(response.data)
    }).catch(error => {
        console.error("카테고리 불러오기 실패", error);
    })

  })

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setProductData({
      ...productData,
      [name]: files ? files[0] : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(productData);
  };

  return (
    <div className="p-6">
      <div className="text-2xl font-bold mb-6">상품등록</div>

      <form onSubmit={handleSubmit} className="border border-gray-200 rounded-md">
        <table className="min-w-full border-collapse">
          <tbody>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">상품명</td>
              <td className="px-4 py-3">
                <input
                  name="productName"
                  value={productData.productName}
                  onChange={handleChange}
                  className="border w-full px-2 py-1"
                  required
                />
              </td>
            </tr>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">상품설명</td>
              <td className="px-4 py-3">
                <textarea
                  name="productDesc"
                  value={productData.productDesc}
                  onChange={handleChange}
                  className="border w-full px-2 py-1"
                  required
                ></textarea>
              </td>
            </tr>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">가격</td>
              <td className="px-4 py-3">
                <input
                  name="price"
                  type="number"
                  value={productData.price}
                  onChange={handleChange}
                  className="border w-full px-2 py-1"
                  required
                />
              </td>
            </tr>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">분류명</td>
              <td className="px-4 py-3">
                <select
                  name="categoryCode"
                  value={productData.categoryCode}
                  onChange={handleChange}
                  className="border px-2 py-1"
                  required
                >
                  <option value="">분류 선택</option>
                    {categories.map((category) => (
                        <option key={category.id} value={category.id}>
                            {category.name}
                        </option>
                    ))}
                </select>
              </td>
            </tr>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">재고</td>
              <td className="px-4 py-3">
                <input
                  name="stock"
                  type="number"
                  value={productData.stock}
                  onChange={handleChange}
                  className="border w-full px-2 py-1"
                  required
                />
              </td>
            </tr>
            <tr>
              <td className="px-4 py-3 bg-gray-100">이미지 업로드</td>
              <td className="px-4 py-3">
                <input
                  name="image"
                  type="file"
                  onChange={handleChange}
                  className="w-full"
                  required
                />
              </td>
            </tr>
          </tbody>
        </table>

        <div className="flex justify-end gap-2 p-4">
          <button type="submit" className="bg-gray-200 px-4 py-2 rounded">등록</button>
          <Link to='/product-management' className="bg-gray-200 px-4 py-2 rounded">취소</Link>
        </div>
      </form>
    </div>
  );
}

export default ProductRegistration;
