import api from "../../api/apiClient";
import { useState, useEffect, useContext } from "react";
import { useNavigate, Link } from "react-router-dom";
import { getDeliveryDate } from "../../utils/commUtils";
import { AuthContext } from "../../context/AuthContext";
import { CheckoutContext } from "../../context/CheckoutContext";

function Cart() {
  const navigate = useNavigate();
  const { userInfo } = useContext(AuthContext);
  const { checkoutState } = useContext(CheckoutContext);
  const items = checkoutState.items;

  const [products, setProducts] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);

  useEffect(() => {
    if (!userInfo?.token || checkoutState.items.length === 0) {
      //navigate("/");
    }
  }, [userInfo?.token, checkoutState.items]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const resShippingInfo = await api.get(`/user/${userInfo.id}`);

        const productPromises = items.map(item =>
          api.get(`/products/${item.product_id}`)
            .then(response => ({
              ...response.data,
              quantity: item.quantity
            }))
        );

        const productResults = await Promise.all(productPromises);
        setProducts(productResults);
        setSelectedItems(productResults.map(p => p.id)); // 기본값: 전부 선택
      } catch (error) {
        console.error("데이터 불러오기 실패", error);
      }
    };

    if (items.length > 0) {
      fetchData();
    } else {
      navigate("/");
    }
  }, [items, userInfo.id]);

  const isAllSelected = products.length > 0 && selectedItems.length === products.length;

  const toggleSelectAll = () => {
    if (isAllSelected) {
      setSelectedItems([]);
    } else {
      setSelectedItems(products.map(p => p.id));
    }
  };

  const toggleSelectItem = (id) => {
    setSelectedItems(prev =>
      prev.includes(id) ? prev.filter(itemId => itemId !== id) : [...prev, id]
    );
  };

  const selectedProducts = products.filter(p => selectedItems.includes(p.id));
  const totalPrice = selectedProducts.reduce(
    (sum, p) => sum + p.price * p.quantity, 0
  );

  const handlePayment = async () => {
    if (selectedProducts.length === 0) {
      alert("선택된 상품이 없습니다.");
      return;
    }

    const orderData = {
      userId: userInfo.id,
      orderItems: selectedProducts.map(product => ({
        productId: product.id,
        quantity: product.quantity,
        orderPrice: product.price
      })),
      totalPrice: totalPrice
    };

    try {
      if (window.confirm("주문 하시겠습니까?")) {
        const response = await api.post(`/order`, orderData);
        if (response.status === 200) {
          const id = response.data.id;
          navigate(`/order-complete/${id}`);
        } else {
          alert("주문에 실패하였습니다.");
        }
      }
    } catch (error) {
      alert("주문에 실패하였습니다.");
    }
  };

  return (
    <div className="max-w-6xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-6">장바구니</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* 좌측 상품 영역 */}
        <div className="md:col-span-2 flex flex-col gap-6">
          <div className="border rounded-lg p-4 shadow-sm bg-white">

            {/* 전체 선택 */}
            <div className="flex items-center mb-4">
              <input
                type="checkbox"
                checked={isAllSelected}
                onChange={toggleSelectAll}
                className="mr-2 scale-125 accent-black"
              />
              <span className="font-semibold text-lg">전체 선택</span>
            </div>

            <hr className="mb-4 border-gray-300" />

            {/* 개별 상품 */}
            {products.map(product => (
              <div
                key={product.id}
                className="flex items-start gap-4 mb-6 border rounded-md p-4 bg-gray-50 relative"
              >
                <input
                  type="checkbox"
                  checked={selectedItems.includes(product.id)}
                  onChange={() => toggleSelectItem(product.id)}
                  className="absolute top-4 left-4 scale-125 accent-black"
                />

                <div className="w-28 h-28 ml-6">
                  <Link to={`/product-detail/${product.id}`}>
                    <img
                      src={
                        product.imagePath
                          ? `http://localhost:8080${product.imagePath}`
                          : "/default-image.png"
                      }
                      alt={product.name}
                      className="w-full h-full object-cover border rounded"
                    />
                  </Link>
                </div>

                <div className="flex flex-col justify-center flex-grow">
                  <Link to={`/product-detail/${product.id}`}>
                    <p className="font-medium text-lg mb-2">{product.name}</p>
                  </Link>
                  <p className="text-blue-600 font-bold text-lg mb-1">
                    {product.price.toLocaleString()}원
                  </p>
                  <p className="text-sm mb-1">수량: {product.quantity}</p>
                  <span className="text-green-600 text-sm font-medium mb-1">무료배송</span>
                  <span className="text-gray-500 text-xs">
                    <span className="font-semibold">{getDeliveryDate()}</span> 도착 예정
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* 우측 결제 정보 */}
        <div className="border rounded-lg p-4 h-fit flex flex-col justify-between bg-white shadow-sm">
          <div>
            <h2 className="font-semibold text-lg mb-2">결제 금액</h2>
            <div className="flex justify-between py-1">
              <span>상품 금액</span>
              <span>{totalPrice.toLocaleString()}원</span>
            </div>
            <div className="flex justify-between py-1 text-gray-700">
              <span>배송비</span>
              <span className="text-blue-600 font-bold">0원</span>
            </div>
            <div className="flex justify-between py-1 font-bold text-lg border-t mt-2 pt-2">
              <span>총 결제 금액</span>
              <span>{totalPrice.toLocaleString()}원</span>
            </div>
          </div>

          <button
            onClick={handlePayment}
            disabled={selectedProducts.length === 0}
            className="mt-6 w-full bg-black text-white py-3 text-lg rounded hover:bg-gray-900 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {totalPrice.toLocaleString()}원 결제하기 ({selectedProducts.length}개)
          </button>
        </div>
      </div>
    </div>
  );
}

export default Cart;
