import { Link } from "react-router-dom"

function Header({ toggleSidebar }) {
    return (
      <div className="bg-gray-200 px-4 py-2 flex items-center justify-between shadow">
        <div className="flex items-center space-x-2">
          <div className="text-2xl cursor-pointer" onClick={toggleSidebar}>☰</div>
          <Link to="/admin" className="bg-gray-300 text-gray-800 px-3 py-1 rounded">관리자 홈</Link>
        </div>
        <Link to="/" className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-1 rounded shadow">쇼핑몰로 이동</Link>


        <nav className="bg-white border-b mt-2">
          <ul className="flex justify-center space-x-24 px-6 overflow-x-auto">
            {categories.map((category) =>
              category === '관리자 페이지' ? (
                <li key={category}>
                  <Link
                    to="/admin"
                    className="block py-3 px-4 text-sm font-medium text-gray-800 bg-gray-200 hover:bg-gray-300"
                  >
                    {category}
                  </Link>
                </li>
              ) : (
                <li
                  key={category}
                  className={`cursor-pointer py-3 px-2 text-sm font-medium border-b-2 transition-colors ${
                    activeTab === category
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-600 hover:text-blue-500'
                  }`}
                  onClick={() => setActiveTab(category)}
                >
                  {category}
                </li>
              )
            )}
          </ul>
        </nav>


      </div>
    );
  }
  
export default Header;
