import { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function ProductManagement() {
  const [products, setProducts] = useState([]);

  const delProduct = async( id ) => {
    try{
      if(window.confirm("상품을 삭제하시겠습니까?")){
        const response = await axios.put(
          `http://localhost:8080/api/products/${id}/delete`
        );
        if(response.status == 200){
          alert("상품이 삭제되었습니다.");
          setProducts((prev) => prev.filter(product => product.id !== id));
        }
      }
    } catch (error) {
        alert("상품삭제에 실패하였습니다.");
    }
  }

  useEffect(() => {
    axios.get("http://localhost:8080/api/products")
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error("상품목록을 불러오지 못하였습니다.", error);
      });
  }, []);

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <div className="text-2xl font-bold">상품관리</div>
        <Link to='/product-registration' className="bg-blue-500 text-white px-4 py-2 rounded">상품등록</Link>
      </div>

      <div className="overflow-x-auto">
        <table className="min-w-full border border-gray-200">
          <thead className="bg-gray-100">
            <tr>
              <th className="px-4 py-2 border w-1/5">상품코드</th>
              <th className="px-4 py-2 border w-1/5">상품정보</th>
              <th className="px-4 py-2 border w-1/5">분류</th>
              <th className="px-4 py-2 border w-1/5">재고</th>
              <th className="px-4 py-2 border w-1/5">관리</th>
            </tr>
          </thead>
          <tbody>
            {products.length > 0 ? (
              products.map((product) => (
                <tr key={product.id}>
                  <td className="px-4 py-2 border">{product.id}</td>
                  <td className="px-4 py-2 border">
                    <div className="flex flex-col items-center space-y-2">
                      <span className="text-center">{product.name}</span>
                      <img
                        src={product.imagePath ? `http://localhost:8080${product.imagePath}` : '/default-image.png'}
                        alt={product.name}
                        className="w-16 h-16 object-cover rounded"
                      />
                    </div>
                  </td>
                  <td className="px-4 py-2 border">{product.categoryName}</td>
                  <td className="px-4 py-2 border">{product.stock}</td>
                  <td className="px-4 py-2 border text-center">
                    <Link to={`/product-edit/${product.id}`} className="inline-flex items-center justify-center bg-blue-500 text-white mr-2 px-4 py-2 rounded no-underline">수정</Link>
                    <button onClick={() => delProduct(product.id)} className="inline-flex items-center justify-center bg-blue-500 text-white px-4 py-2 rounded">삭제</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="text-center py-8 text-gray-500">
                  No data available
                </td>
              </tr>
            )}
          </tbody>
        </table>

        <div className="flex justify-end mt-4 items-center gap-2 text-sm text-gray-700">
          <span>Rows per page:</span>
          <select className="border rounded px-2 py-1">
            <option>10</option>
            <option>20</option>
            <option>30</option>
          </select>
          <span>-</span>
          <button className="px-2">&lt;</button>
          <button className="px-2">&gt;</button>
        </div>
      </div>
    </div>
  );
}

export default ProductManagement;
