import { useState } from 'react';

const categories = [
  '분류1', '분류2', '분류3', '분류4', '분류5', '분류6', '분류7', '관리자 페이지'
];

const products = [
  { title: 'Ranking', label: '판매랭킹순' },
  { title: 'Living Room', label: 'LED 거실등' },
  { title: 'Room Lamp', label: 'LED 방등' },
  { title: 'Electrical Materials', label: '전기재료' },
];

function Layout() {
  const [activeTab, setActiveTab] = useState('분류1');

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow p-4 flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <button className="border px-4 py-1 rounded">로그인</button>
          <button className="border px-4 py-1 rounded">회원가입</button>
        </div>
        <h1 className="text-2xl font-bold">ShoppingMall</h1>
        <div className="w-24" />
      </header>

      <nav className="bg-white border-b mt-2">
        <ul className="flex space-x-6 px-6 overflow-x-auto">
          {categories.map((category) => (
            <li
              key={category}
              className={`cursor-pointer py-4 px-2 text-sm font-medium border-b-2 transition-colors ${
                activeTab === category
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-600 hover:text-blue-500'
              }`}
              onClick={() => setActiveTab(category)}
            >
              {category}
            </li>
          ))}
        </ul>
      </nav>

      <main className="p-6 space-y-10">
        {products.map((product, index) => (
          <div key={index}>
            <h2 className="text-xl font-bold">{product.title} <span className="text-sm font-normal text-gray-500">{product.label}</span></h2>
            <hr className="mt-2" />
          </div>
        ))}
      </main>
    </div>
  );
}

export default Layout;
