import api from "../../api/apiClient";
import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../../context/AuthContext";
import { Link } from "react-router-dom";

function Orders() {
  const { userInfo } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await api.get(`/orders/user/${userInfo.id}`);
        setOrders(response.data);
      } catch (error) {
        console.error("주문 내역 불러오기 실패", error);
      }
    };

    if (userInfo?.id) {
      fetchOrders();
    }
  }, [userInfo]);

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-6">주문 내역</h1>

      {orders.length === 0 ? (
        <p className="text-gray-500">주문 내역이 없습니다.</p>
      ) : (
        orders.map((order) => (
          <div key={order.id} className="mb-8 border-b pb-6">
            <p className="text-sm text-gray-500 mb-2">
              {new Date(order.orderDate).toLocaleDateString()} | {order.status}
            </p>

            {order.orderItems.map((item) => (
              <div key={item.id} className="flex gap-4 mb-4 items-center">
                <div className="w-24 h-24 flex-shrink-0">
                  <Link to={`/product-detail/${item.product.id}`}>
                    <img
                      src={
                        item.product.imagePath
                          ? `http://localhost:8080${item.product.imagePath}`
                          : "/default-image.png"
                      }
                      alt={item.product.name}
                      className="w-full h-full object-cover border rounded"
                    />
                  </Link>
                </div>

                <div className="flex flex-col justify-center flex-grow">
                  <Link to={`/product-detail/${item.product.id}`}>
                    <p className="font-medium text-lg mb-1">
                      {item.product.name}
                    </p>
                  </Link>
                  <p className="text-sm text-gray-500 mb-1">
                    수량: {item.quantity}개
                  </p>
                  <p className="text-blue-600 font-bold text-base">
                    {(item.orderPrice * item.quantity).toLocaleString()}원
                  </p>
                </div>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
}

export default Orders;
