import api from "../../api/apiClient";
import { useState, useEffect } from 'react';
import { useParams } from "react-router-dom";

function ProductDetail() {
  const { id } = useParams();
  const [quantity, setQuantity] = useState(1);
  const price = 30900;

  useEffect(() => {
    console.log(id);
  }, [id]);

  return (
    <div className="max-w-7xl mx-auto p-4 grid grid-cols-1 lg:grid-cols-2 gap-8">
      {/* 왼쪽 - 이미지 섹션 */}
      <div className="flex flex-col lg:flex-row gap-4">
        <div className="flex-1">
          <img
            src="/default-image.png"
            alt="메인 상품 이미지"
            className="w-full h-auto object-cover rounded"
          />
        </div>
      </div>

      {/* 오른쪽 - 상품 정보 */}
      <div className="space-y-4">
        <div className="text-sm text-gray-500">분류1</div>
        <h1 className="text-2xl font-bold">
          상품명 (예정)
        </h1>

        <div className="flex items-center space-x-2">
          <div className="text-blue-600 font-semibold text-lg">61%</div>
          <div className="text-xl font-bold text-red-500">{price.toLocaleString()}원</div>
          <div className="line-through text-gray-400 text-sm">79,900원</div>
        </div>

        <div className="text-sm text-gray-600">무료배송 · 5/9(목) 도착 예정</div>

        {/* 수량 선택 */}
        <div className="space-y-1">
          <label className="block text-sm font-medium text-gray-700">수량</label>
          <div className="flex items-center border rounded w-32 overflow-hidden">
            <button
              type="button"
              onClick={() => setQuantity(prev => Math.max(1, prev - 1))}
              className="w-10 h-10 flex justify-center items-center text-xl border-r hover:bg-gray-100"
            >
              –
            </button>
            <input
              type="number"
              value={quantity}
              onChange={(e) => {
                const value = Math.max(1, parseInt(e.target.value) || 1);
                setQuantity(value);
              }}
              className="w-full text-center outline-none"
              min="1"
            />
            <button
              type="button"
              onClick={() => setQuantity(prev => prev + 1)}
              className="w-10 h-10 flex justify-center items-center text-xl border-l hover:bg-gray-100"
            >
              +
            </button>
          </div>
        </div>

        {/* 최종 결제 금액 */}
        <div className="text-right text-lg font-semibold text-gray-800">
          총 결제금액: {(price * quantity).toLocaleString()}원
        </div>

        {/* 주문 버튼 */}
        <div className="pt-2 flex justify-center">
            <button className="w-48 bg-gray-800 hover:bg-gray-900 text-white py-3 rounded">
                바로구매
            </button>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
