import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../../context/AuthContext";
import api from "../../api/apiClient";
import { Link } from "react-router-dom";

function Orders() {
  const { userInfo } = useContext(AuthContext);
  const [ordersByOrderId, setOrdersByOrderId] = useState({});

  // 상태 텍스트 계산 함수
  const getOrderStatusText = (status, orderDate) => {
    const today = new Date();
    const orderDateObj = new Date(orderDate);

    if (orderDateObj <= today) {
      return "배송완료";
    }

    if (status === "paid") {
      return "결제완료";
    }

    return status;
  };

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await api.get(`/user/${userInfo.id}/orders`);

        const ordersMap = {};
        response.data.forEach((item) => {
          const orderId = item.orderId;

          if (!ordersMap[orderId]) {
            ordersMap[orderId] = {
              orderId: item.orderId,
              orderDate: item.orderDate,
              totalPrice: item.totalPrice,
              status: item.status,
              items: [],
            };
          }

          ordersMap[orderId].items.push(item);
        });

        setOrdersByOrderId(ordersMap);
      } catch (error) {
        console.error("주문 내역 불러오기 실패", error);
      }
    };

    if (userInfo?.id) {
      fetchOrders();
    }
  }, [userInfo]);

  const orderList = Object.values(ordersByOrderId);

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-6">주문 내역</h1>

      {orderList.length === 0 ? (
        <p className="text-gray-500">주문 내역이 없습니다.</p>
      ) : (
        orderList.map((order) => (
          <div key={order.orderId} className="mb-8 border-b pb-6">
            <p className="text-sm text-gray-500 mb-2">
              {new Date(order.orderDate).toLocaleDateString()} |{" "}
              {getOrderStatusText(order.status, order.orderDate)}
            </p>

            {order.items.map((item) => (
              <div key={item.orderItemId} className="flex gap-4 mb-4 items-center">
                <div className="w-24 h-24 flex-shrink-0">
                  <Link to={`/product-detail/${item.productId}`}>
                    <img
                      src={
                        item.imagePath
                          ? `http://localhost:8080${item.imagePath}`
                          : "/default-image.png"
                      }
                      alt={item.productName}
                      className="w-full h-full object-cover border rounded"
                    />
                  </Link>
                </div>

                <div className="flex flex-col justify-center flex-grow">
                  <Link to={`/product-detail/${item.productId}`}>
                    <p className="font-medium text-lg mb-1">{item.productName}</p>
                  </Link>
                  <p className="text-sm text-gray-500 mb-1">
                    수량: {item.quantity}개
                  </p>
                  <p className="text-blue-600 font-bold text-base">
                    {(item.orderPrice * item.quantity).toLocaleString()}원
                  </p>
                </div>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
}

export default Orders;
