import api from "../../api/apiClient";
import { useState, useEffect, useContext } from "react";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import { getDeliveryDate } from "../../utils/commUtils";
import { AuthContext } from "../../context/AuthContext";

function Checkout() {
  const navigate = useNavigate();
  const { id } = useParams();
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const quantity = Number(searchParams.get("quantity") ?? 0);
  const { userInfo }= useContext(AuthContext);

  const [product, setProduct] = useState({
    name: "",
    price: "",
    imagePath: ""
  })

  const [user, setUser] = useState({
    name:"",
    address:"",
    phone:""
  })

  // 이것도 context로 email 가져와서 email로 DB로 User 정보 불러오는게 맞음...
  const [shippingInfo, setShippingInfo] = useState({
    name: "조재윤",
    phone: "010-8725-3094",
    address: "서울 영등포구 양평로12길 10-5 (당산동6가) 스타빌, 916호"
  });

  // product하고 shippingInfo 2개 합쳐서 useEffect로 가져오기...

  const totalPrice = product.price * quantity;

  useEffect(() => {
    const fetchData = async () => {

      try{
        const[resProduct, resUser] = await Promise.all([
          api.get(`/products/${id}`),
          api.get(`/user/${email}`)
        ]);
        setProduct(resProduct.data);
        setUser(resUser.data)
      }catch(error){
        console.error('데이터 불러오기 실패', error);        
      }
    }

    fetchData();
  }, [id])


  const handlePayment = () => {
    alert("결제가 완료되었습니다.");
    navigate("/");
  };


  return (
    <div className="max-w-6xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-6">주문 / 결제</h1>
  
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* 좌측: 배송지 + 상품정보 */}
        <div className="md:col-span-2 flex flex-col gap-6">
          {/* 배송 정보 */}
          <div className="border rounded-lg p-4">
            <h2 className="font-semibold mb-2">배송지 정보</h2>
            <p>{shippingInfo.name}</p>
            <p>{shippingInfo.address}</p>
            <p>{shippingInfo.phone}</p>
          </div>
  
          {/* 주문 상품 정보 */}
          <div className="border rounded-lg p-4">
            <h2 className="font-semibold mb-2">주문 상품</h2>
            <div className="flex gap-6 items-center">
              {/* 이미지 영역 */}
              <div className="flex-shrink-0 w-48 h-48">
                <img
                  src={product.imagePath ? `http://localhost:8080${product.imagePath}` : '/default-image.png'}
                  alt={product.name}
                  className="w-full h-full object-cover border rounded"
                />
              </div>
  
              {/* 텍스트 영역 */}
              <div className="flex flex-col justify-center flex-grow">
                <p className="font-medium text-lg mb-2">{product.name}</p>
                <p className="mb-1">수량: {quantity}</p>
                <p className="text-blue-600 font-bold text-lg mb-2">{product.price.toLocaleString()}원</p>
                <span className="text-green-600 text-sm font-medium mb-2">무료배송</span>
                <span className="text-gray-500 text-xs">
                  <span className="font-semibold">{getDeliveryDate()}</span> 도착 예정
                </span>
              </div>
            </div>
          </div>
        </div>
  
        {/* 우측: 결제 정보 */}
        <div className="border rounded-lg p-4 h-fit flex flex-col justify-between">
          <div>
            <h2 className="font-semibold mb-2">결제 금액</h2>
            <div className="flex justify-between py-1">
              <span>상품 금액</span>
              <span>{totalPrice.toLocaleString()}원</span>
            </div>
            <div className="flex justify-between py-1 text-gray-700">
              <span>배송비</span>
              <span>0원</span>
            </div>
            <div className="flex justify-between py-1 font-bold text-lg">
              <span>총 결제 금액</span>
              <span>{totalPrice.toLocaleString()}원</span>
            </div>
          </div>
  
          {/* 결제 버튼 */}
          <button
            onClick={handlePayment}
            className="mt-6 w-full bg-black text-white py-3 text-lg rounded hover:bg-gray-900"
          >
            {totalPrice.toLocaleString()}원 결제하기
          </button>
        </div>
      </div>
    </div>
  );
  
}

export default Checkout;
