import { useEffect, useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { CheckoutContext } from '../../context/CheckoutContext';
import { CartContext } from '../../context/CartContext';

function Header({ categories }) {
  const { userInfo, dispatch: authDispatch } = useContext(AuthContext);
  const { dispatch: checkoutDispatch } = useContext(CheckoutContext);
  const { dispatch: cartDispatch } = useContext(CartContext);
  const [keyword, setKeyword] = useState('');
  const navigate = useNavigate();
  const isAdmin = userInfo?.roles?.includes("ROLE_ADMIN");

  const renderedCategories = isAdmin
    ? [...categories, { id: 999, name: '관리자 페이지', isAdminLink: true }]
    : [...categories];

  const handelLogout = () => {
    authDispatch({ type: "LOGOUT" });
    checkoutDispatch({ type: "CLEAR_ITEMS" });
    cartDispatch({ type: "CLEAR_CART" });

    localStorage.removeItem("checkoutItems");
    localStorage.removeItem("cartItems");
    localStorage.removeItem("cartMerged");
    alert("로그아웃 되었습니다.");
    navigate('/');
  };

  const handleSearch = () => {
    if (keyword.trim()) {
      navigate(`/search?query=${encodeURIComponent(keyword)}`);
    } else {
      alert("검색어를 입력해주세요.");
    }
  };

  return (
    <>
      {/* 헤더 */}
      <header className="bg-[#f9fbff] shadow-md border-b fixed top-0 left-0 right-0 z-50">
        <div className="max-w-screen-xl mx-auto px-4 md:px-8 py-4 flex items-center justify-between">
          {/* 로고 */}
          <h1 className="text-2xl font-bold text-blue-600 tracking-tight">
            <Link to="/">ShoppingMall</Link>
          </h1>

          {/* 검색창 */}
          <div className="absolute left-1/2 -translate-x-1/2 flex items-center space-x-2">
            <input
              type="text"
              placeholder="통합검색"
              className="w-64 px-4 py-2 rounded-full border border-blue-200 bg-white text-sm shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent transition"
              value={keyword}
              onChange={(e) => setKeyword(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
            />
            <button
              onClick={handleSearch}
              className="px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-full text-sm font-semibold shadow-md transition"
            >
              검색
            </button>
          </div>

          {/* 사용자 메뉴 */}
          <div className="flex items-center gap-2 text-sm font-medium text-gray-700">
            {userInfo.token ? (
              <>
                <Link
                  to="/cart"
                  className="px-4 py-2 rounded-full hover:bg-blue-50 hover:text-blue-600 transition"
                >
                  장바구니
                </Link>
                <div className="relative group">
                  <button className="px-4 py-2 rounded-full hover:bg-blue-50 hover:text-blue-600 transition">
                    내 정보
                  </button>
                  <div className="absolute right-0 mt-2 hidden group-hover:flex flex-col bg-white border rounded-lg shadow-lg z-10 w-44">
                    <Link to="/profile-edit" className="px-4 py-2 hover:bg-blue-50">회원정보 수정</Link>
                    <Link to="/orders" className="px-4 py-2 hover:bg-blue-50">주문내역 조회</Link>
                  </div>
                </div>
                <button
                  onClick={handelLogout}
                  className="px-4 py-2 rounded-full hover:bg-blue-50 hover:text-blue-600 transition"
                >
                  로그아웃
                </button>
              </>
            ) : (
              <>
                <Link
                  to="/cart"
                  className="px-4 py-2 rounded-full hover:bg-blue-50 hover:text-blue-600 transition"
                >
                  장바구니
                </Link>
                <Link
                  to="/login"
                  className="px-4 py-2 rounded-full hover:bg-blue-50 hover:text-blue-600 transition"
                >
                  로그인
                </Link>
                <Link
                  to="/signUp"
                  className="px-4 py-2 rounded-full hover:bg-blue-50 hover:text-blue-600 transition"
                >
                  회원가입
                </Link>
              </>
            )}
          </div>
        </div>
      </header>

      {/* 카테고리 네비게이션 */}
      <nav className="bg-white border-b shadow-sm fixed top-[80px] left-0 right-0 z-40">
        <ul className="flex justify-center gap-6 md:gap-10 overflow-x-auto px-4 md:px-8 py-3 text-sm font-semibold text-gray-600 whitespace-nowrap">
          {renderedCategories.map((category, index) =>
            category.isAdminLink ? (
              <li key={category.id}>
                <Link
                  to="/admin"
                  className="bg-blue-50 hover:bg-blue-100 text-blue-700 px-4 py-2 rounded-full transition"
                >
                  {category.name}
                </Link>
              </li>
            ) : (
              <li key={category.id}>
                <Link
                  to={`/category/${category.id}?index=${index}`}
                  className="hover:text-blue-600 px-3 py-1 transition-colors"
                >
                  {category.name}
                </Link>
              </li>
            )
          )}
        </ul>
      </nav>

      {/* 여백 확보 */}
      <div className="h-[128px]"></div>
    </>
  );
}

export default Header;
