import api from "../../api/apiClient";
import { useState, useEffect } from "react";
import { useNavigate, useLocation, useParams } from "react-router-dom";

function Checkout() {
  const navigate = useNavigate();
  const { id } = useParams();
  const location = useLocation();

  const searchParams = new URLSearchParams(location.search);
  const quantity = Number(searchParams.get("quantity") ?? 0);

  // 상품 정보는 무조건 DB불러오는게 맞음... , 대신 수량만 이전페이지꺼 가져오기...
  const product = {
    name: "",
    price: "",
    imagePath: "/default-image.png"
  };

  // 이것도 context로 email 가져와서 email로 DB로 User 정보 불러오는게 맞음...
  const [shippingInfo, setShippingInfo] = useState({
    name: "조재윤",
    phone: "010-8725-3094",
    address: "서울 영등포구 양평로12길 10-5 (당산동6가) 스타빌, 916호"
  });

  // product하고 shippingInfo 2개 합쳐서 useEffect로 가져오기...

  const totalPrice = product.price * quantity;

  useEffect(() => {
    api.get(`/products/${id}`)
  }, [])


  const handlePayment = () => {
    alert("결제가 완료되었습니다.");
    navigate("/");
  };

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-6">주문 / 결제</h1>

      {/* 배송 정보 */}
      <div className="border rounded-lg p-4 mb-6">
        <h2 className="font-semibold mb-2">배송지 정보</h2>
        <p>{shippingInfo.name}</p>
        <p>{shippingInfo.address}</p>
        <p>{shippingInfo.phone}</p>
      </div>

      {/* 주문 상품 정보 */}
      <div className="border rounded-lg p-4 mb-6">
        <h2 className="font-semibold mb-2">주문 상품</h2>
        <div className="flex gap-4">
          <img src={product.imagePath} alt={product.name} className="w-24 h-24 border rounded" />
          <div className="flex flex-col justify-between">
            <p className="font-medium">{product.name}</p>
            <p>수량: {quantity}</p>
            <p className="text-blue-600 font-bold">{product.price.toLocaleString()}원</p>
          </div>
        </div>
      </div>

      {/* 결제 금액 */}
      <div className="border rounded-lg p-4 mb-6">
        <h2 className="font-semibold mb-2">결제 금액</h2>
        <div className="flex justify-between py-1">
          <span>상품 금액</span>
          <span>{totalPrice.toLocaleString()}원</span>
        </div>
        <div className="flex justify-between py-1 text-gray-700">
        <span>배송비</span>
        <span className="text-blue-600 font-semibold">무료</span>
        </div>
        <div className="flex justify-between py-1 font-bold text-lg">
          <span>총 결제 금액</span>
          <span>{totalPrice.toLocaleString()}원</span>
        </div>
      </div>

      {/* 결제 버튼 */}
      <div className="text-center mt-8">
        <button
          onClick={handlePayment}
          className="w-60 bg-black text-white py-3 text-lg rounded hover:bg-gray-900"
        >
          {totalPrice.toLocaleString()}원 결제하기
        </button>
      </div>
    </div>
  );
}

export default Checkout;
