import { useEffect, useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { CheckoutContext } from '../../context/CheckoutContext';
import { CartContext } from '../../context/CartContext';

function Header({ categories }) {
  const { userInfo, dispatch: authDispatch } = useContext(AuthContext);
  const { dispatch: checkoutDispatch } = useContext(CheckoutContext);
  const { dispatch: cartDispatch } = useContext(CartContext);
  const isAdmin = userInfo?.roles?.includes("ROLE_ADMIN");
  const [keyword, setKeyword] = useState('');
  const navigate = useNavigate();

  const renderedCategories = isAdmin
    ? [...categories, { id: 999, name: '관리자 페이지', isAdminLink: true }]
    : [...categories];

  const handelLogout = () => {
    authDispatch({ type: "LOGOUT" });
    checkoutDispatch({ type: "CLEAR_ITEMS" });
    cartDispatch({ type: "CLEAR_CART" });
    localStorage.removeItem("checkoutItems");
    localStorage.removeItem("cartItems");
    localStorage.removeItem("cartMerged");
    alert("로그아웃 되었습니다.");
    navigate('/');
  };

  const handleSearch = () => {
    if (keyword.trim()) {
      navigate(`/search?query=${encodeURIComponent(keyword)}`);
    } else {
      alert("검색어를 입력해주세요.");
    }
  };

  return (
    <>
      <header className="bg-white shadow-sm border-b fixed top-0 left-0 right-0 z-50">
        <div className="max-w-screen-xl mx-auto px-4 md:px-8 py-4 flex items-center justify-between">
          <h1 className="text-xl font-bold text-gray-900">
            <Link to="/">ShoppingMall</Link>
          </h1>

          <div className="hidden md:flex items-center space-x-2 w-1/2">
            <input
              type="text"
              placeholder="통합검색"
              className="flex-grow border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
              value={keyword}
              onChange={(e) => setKeyword(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
            />
            <button
              onClick={handleSearch}
              className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-md text-sm"
            >
              검색
            </button>
          </div>

          <div className="flex items-center gap-2 text-sm text-gray-800">
            {userInfo.token ? (
              <>
                <Link to="/cart" className="hover:text-blue-500">장바구니</Link>
                <div className="relative group">
                  <button className="hover:text-blue-500">내 정보</button>
                  <div className="absolute hidden group-hover:block bg-white border rounded shadow-md mt-2 right-0 z-50 text-sm">
                    <Link to="/profile-edit" className="block px-4 py-2 hover:bg-gray-100">회원정보 수정</Link>
                    <Link to="/orders" className="block px-4 py-2 hover:bg-gray-100">주문내역 조회</Link>
                  </div>
                </div>
                <button onClick={handelLogout} className="hover:text-blue-500">로그아웃</button>
              </>
            ) : (
              <>
                <Link to="/cart" className="hover:text-blue-500">장바구니</Link>
                <Link to="/login" className="hover:text-blue-500">로그인</Link>
                <Link to="/signUp" className="hover:text-blue-500">회원가입</Link>
              </>
            )}
          </div>
        </div>

        <div className="md:hidden px-4 pb-3">
          <input
            type="text"
            placeholder="통합검색"
            className="w-full border border-gray-300 rounded-md px-3 py-2 mt-2"
            value={keyword}
            onChange={(e) => setKeyword(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
          />
        </div>
      </header>

      <nav className="bg-white border-t border-b mt-[92px]">
        <div className="flex overflow-x-auto px-4 md:px-8 space-x-6 py-3">
          {renderedCategories.map((category, index) =>
            category.isAdminLink ? (
              <Link key={category.id} to="/admin" className="text-sm text-gray-800 whitespace-nowrap hover:text-blue-500">
                {category.name}
              </Link>
            ) : (
              <Link
                key={category.id}
                to={`/category/${category.id}?index=${index}`}
                className="text-sm text-gray-600 hover:text-blue-500 whitespace-nowrap"
              >
                {category.name}
              </Link>
            )
          )}
        </div>
      </nav>

      <div className="h-[136px]"></div>
    </>
  );
}

export default Header;
