import api from "../../api/apiClient";
import { useState, useEffect } from 'react';
import { useParams } from "react-router-dom";

function ProductDetail() {
  const { id } = useParams();
  const [quantity, setQuantity] = useState(1);
  const price = 30900;

  useEffect(() => {
    console.log(id);
  }, [id]);

  const getDeliveryDate = () => {
    const date = new Date();
    date.setDate(date.getDate() + 3);
    const week = ['일', '월', '화', '수', '목', '금', '토'];
    const formatted = `${date.getMonth() + 1}/${date.getDate()}(${week[date.getDay()]})`;
    return formatted;
  };

  return (
    <div className="max-w-6xl mx-auto p-8">
      <div className="flex flex-col lg:flex-row gap-12 items-start">
        {/* 왼쪽 - 이미지 */}
        <div className="w-full lg:max-w-md mx-auto">
          <img
            src="/default-image.png"
            alt="메인 상품 이미지"
            className="w-full h-auto object-cover border rounded"
          />
        </div>

        {/* 오른쪽 - 상품 정보 */}
        <div className="flex-1 flex flex-col justify-start items-start gap-6 text-base lg:text-lg">
          <div className="text-gray-500 text-sm">분류1</div>

          <h1 className="text-3xl font-bold text-gray-900">
            상품명 (예정)
          </h1>

          <div className="text-2xl font-bold text-blue-600">
            {price.toLocaleString()}원
          </div>

          <div className="text-gray-600 text-base">
            무료배송 · <span className="font-semibold">{getDeliveryDate()}</span> 도착 예정
          </div>

          {/* 수량 선택 */}
          <div className="w-full">
            <label className="block text-gray-700 mb-1 font-medium">수량</label>
            <div className="flex items-center border rounded w-36 overflow-hidden">
              <button
                type="button"
                onClick={() => setQuantity(prev => Math.max(1, prev - 1))}
                className="w-10 h-10 flex justify-center items-center text-xl border-r hover:bg-gray-100"
              >
                –
              </button>
              <input
                type="number"
                value={quantity}
                onChange={(e) => {
                  const value = Math.max(1, parseInt(e.target.value) || 1);
                  setQuantity(value);
                }}
                className="w-full text-center outline-none"
                min="1"
              />
              <button
                type="button"
                onClick={() => setQuantity(prev => prev + 1)}
                className="w-10 h-10 flex justify-center items-center text-xl border-l hover:bg-gray-100"
              >
                +
              </button>
            </div>
          </div>

          {/* 총 결제 금액 */}
          <div className="w-full text-right font-semibold text-xl text-gray-800">
            총 결제금액: {(price * quantity).toLocaleString()}원
          </div>

          {/* 구매 버튼 */}
          <div className="w-full flex justify-center">
            <button className="w-48 bg-gray-800 hover:bg-gray-900 text-white py-3 rounded text-lg">
              바로구매
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
