import { useState, useEffect, useRef } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

function UserEdit() {

  const { id } = useParams();
  const [userData, setUserData] = useState({
    name: "",
    description: "",
    price: "",
    stock: "",
    uploadImage: null,
    imagePath: "",
    categoryId: "",
  });

  const [categories, setCategories] = useState([]);

  useEffect(() => {
      const fetchData = async () => {
        try{
          const [resProduct, resCategories] = await Promise.all([
            axios.get(`http://localhost:8080/api/products/${id}`),
            axios.get(`http://localhost:8080/api/categories`)
          ]);
          setCategories(resCategories.data);
          setUserData(resProduct.data);
        } catch(error) {
          console.error('데이터 불러오기 실패', error);
        }
      };

      fetchData();
      
  }, [id])

  const modProc = async() => {
    try{
        if(window.confirm("상품을 수정하시겠습니까?")){

          console.log("ID", id);

            const param = new FormData();
            param.append("name", userData.name);
            param.append("description", userData.description);
            param.append("price", userData.price);
            param.append("stock", userData.stock);
            param.append("categoryId", userData.categoryId);

            if (userData.uploadImage) {
              param.append("uploadImage", userData.uploadImage);
            }

            const response = await axios.put(
              `http://localhost:8080/api/products/${id}`, 
               param, 
               {
                 headers: {
                  "Content-Type": "multipart/form-data",
                 }
               }
            );

            if (response.status === 200) {
                alert("수정 되었습니다.");
                window.location.href = '/product-management';
            }
        }
    } catch (error) {
        alert("수정에 실패하였습니다.");
    }
  }

  const handleChange = (e) => {
    const { name, value, files, type } = e.target;
  
    if (type === "file") {
      const file = files?.[0];
      if (file) {
        setUserData(prev => ({
          ...prev,
          [name]: file,
        }));
        setPreviewImage(URL.createObjectURL(file));
        setUploadedFileName(file.name);
      } else {
        setPreviewImage(null);
        setUploadedFileName("");
      }
    } else {
      setUserData(prev => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  return (
    <div className="p-6">
      <div className="text-2xl font-bold mb-6">회원정보 수정</div>

        <table className="min-w-full border-collapse border border-gray-200 rounded-md">
          <tbody>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">이메일</td>
              <td className="px-4 py-3">
                <input
                  name="name"
                  value={userData.name}
                  onChange={handleChange}
                  className="border w-full px-2 py-1"
                  required
                />
              </td>
            </tr>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">이름</td>
              <td className="px-4 py-3">
                <textarea
                  name="description"
                  value={userData.description}
                  onChange={handleChange}
                  className="border w-full px-2 py-1"
                  required
                ></textarea>
              </td>
            </tr>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">주소</td>
              <td className="px-4 py-3">
                <input
                  name="price"
                  type="number"
                  value={userData.price}
                  onChange={handleChange}
                  className="border w-full px-2 py-1"
                  required
                />
              </td>
            </tr>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">전화번호</td>
              <td className="px-4 py-3">
                <select
                  name="categoryId"
                  value={userData.categoryId}
                  onChange={handleChange}
                  className="border px-2 py-1"
                  required
                >
                  <option value="">분류 선택</option>
                    {categories.map((category) => (
                        <option key={category.id} value={category.id}>
                            {category.name}
                        </option>
                    ))}
                </select>
              </td>
            </tr>
          </tbody>
        </table>

        <div className="flex justify-end gap-2 p-4">
          <button type="button" onClick={modProc} className="bg-gray-200 px-4 py-2 rounded">수정</button>
          <Link to='/user-info' className="bg-gray-200 px-4 py-2 rounded">취소</Link>
        </div>
    </div>
  );
}

function extractFileName(path) {
  const fileName = path.split('/').pop();
  return fileName.split('_').pop();
}

export default UserEdit;
