import { useState, useEffect } from 'react';
import { useParams, useLocation } from "react-router-dom";

function Category() {
  const { id } = useParams();
  const { state } = useLocation();
  const categoryName = state?.name;
  const categoryOrd = Number(state?.index) + 1;

  // 가짜 이미지 인덱스 리스트 (나중에 실제 상품 데이터로 교체 가능)
  const [imageList, setImageList] = useState([1, 2, 3, 4]);

  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 8;

  useEffect(() => {
    // 예: 비동기로 상품 받아서 setImageList([...]) 가능
  }, []);

  // 페이징된 데이터
  const paginated = productList.slice(
    (currentPage - 1) * pageSize,
    currentPage * pageSize
  );

  const totalPages = Math.ceil(productList.length / pageSize);

  return (
    <div className="px-4 md:px-8">
      <main className="max-w-screen-xl mx-auto space-y-10 mt-8">
        <div>
          <h2 className="text-xl font-bold">
            Category{categoryOrd}{' '}
            <span className="text-sm font-normal text-gray-500">
              {categoryName}
            </span>
          </h2>
          <hr className="mt-2" />
        </div>

        {/* 상품 목록 */}
        <div className="grid grid-cols-2 sm:grid-cols-4 gap-6 mt-4 relative">
          {imageList.map((imgIndex) => (
            <div
              key={imgIndex}
              className="border rounded overflow-hidden flex justify-center items-center aspect-square"
            >
              <img
                src={'/default-image.png'}
                alt={`Product ${imgIndex}`}
                className="w-full h-full object-contain p-4"
              />
            </div>
          ))}
        </div>

        {/* 페이징 */}
        <div className="flex justify-center mt-6 space-x-2">
          {Array.from({ length: totalPages }, (_, i) => (
            <button
              key={i}
              onClick={() => setCurrentPage(i + 1)}
              className={`px-3 py-1 text-sm border rounded ${
                currentPage === i + 1
                  ? 'bg-gray-800 text-white'
                  : 'text-gray-700 border-gray-300 hover:bg-gray-100'
              }`}
            >
              {i + 1}
            </button>
          ))}
        </div>
      </main>
    </div>
  );
}

export default Category;
