import React, { createContext, useReducer, useEffect } from "react";

const initialState = {
  name: localStorage.getItem("name") || null,
  email: localStorage.getItem("email") || null,
  token: localStorage.getItem("token") || null,
};

const authReducer = (state, action) => {
  switch (action.type) {
    case "LOGIN":
      return { ...state, name: action.payload.name, email: action.payload.email, token: action.payload.token };
    case "LOGOUT":
      return { ...state, name: null, email: null, token: null };
    default:
      return state;
  }
};

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {

  const [state, dispatch] = useReducer(authReducer, initialState);

  useEffect(() => {
    if (state.token) {
      localStorage.setItem("name", state.name);
      localStorage.setItem("email", state.email);
      localStorage.setItem("token", state.token);
    } else {
      localStorage.removeItem("name");
      localStorage.removeItem("email");
      localStorage.removeItem("token");
    }
  }, [state.name, state.email, state.token]);

  // context api를 통해 하위 컴포넌트로 state와 dispatch 전달
  return (
    <AuthContext.Provider value={{ state, dispatch }}>
      {children}
    </AuthContext.Provider>
  );

};
