import { useState, useEffect } from 'react';
import { useOutletContext, Link } from 'react-router-dom';
import api from '../../api/apiClient';

function ShopMain() {
  const { categories } = useOutletContext();
  const [productList, setProductList] = useState([]);

  useEffect(() => {
    api.get('/products/top-by-category?limit=4')
      .then(response => {
        setProductList(response.data);
      })
      .catch(error => {
        console.error("상품목록을 불러오지 못하였습니다.", error);
      });
  }, []);

  return (
    <main className="max-w-screen-xl mx-auto px-4 md:px-8 mt-8 space-y-12">
      {categories.map((category, index) => (
        <section key={category.id}>
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg md:text-xl font-semibold text-gray-800">
              {category.name}
            </h2>
            <Link
              to={`/category/${category.id}?index=${index}`}
              className="text-sm text-blue-500 hover:underline"
            >
              더보기
            </Link>
          </div>

          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
            {productList
              .filter(product => product.categoryId === category.id)
              .map((product) => (
                <Link
                  key={product.id}
                  to={`/product-detail/${product.id}`}
                  className="bg-white border rounded-md shadow-sm hover:shadow-md transition overflow-hidden"
                >
                  <div className="aspect-square overflow-hidden bg-gray-50 flex justify-center items-center">
                    <img
                      src={product.imagePath ? `http://localhost:8080${product.imagePath}` : '/default-image.png'}
                      alt={product.name}
                      className="object-contain max-h-full"
                    />
                  </div>
                  <div className="p-2 text-sm">
                    <h3 className="truncate font-medium text-gray-800">{product.name || '상품명 없음'}</h3>
                    <p className="text-gray-500 mt-1">
                      {product.price != null ? `${product.price.toLocaleString()}원` : '가격 미정'}
                    </p>
                  </div>
                </Link>
              ))}
          </div>
        </section>
      ))}
    </main>
  );
}

export default ShopMain;
