import { useState } from 'react';

function Header() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  return (
    <div className="flex">
      {/* 사이드바 */}
      {isSidebarOpen && (
        <div className="w-48 bg-white shadow h-screen p-4 space-y-2">
          <h2 className="text-gray-600 font-semibold">목록</h2>
          <button className="w-full text-left px-2 py-1 hover:bg-gray-100 rounded">회원관리</button>
          <button className="w-full text-left px-2 py-1 hover:bg-gray-100 rounded">쇼핑몰관리</button>
          <button className="w-full text-left px-2 py-1 hover:bg-gray-100 rounded">쇼핑몰현황</button>
        </div>
      )}

      {/* 오른쪽 전체 영역 */}
      <div className="flex-1">
        <div className="bg-gray-200 px-4 py-2 flex items-center justify-between shadow">
          {/* 왼쪽 묶음 */}
          <div className="flex items-center space-x-2">
            <div className="text-2xl cursor-pointer" onClick={() => setIsSidebarOpen(!isSidebarOpen)}>☰</div>
            <button className="bg-gray-300 text-gray-800 px-3 py-1 rounded">관리자 페이지 (메인)</button>
            <button className="bg-gray-300 text-gray-800 px-3 py-1 rounded">로그인</button>
          </div>

          {/* 오른쪽 버튼 */}
          <button className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-1 rounded shadow">
            홈페이지로 이동
          </button>
        </div>

        {/* 본문 영역 (예시) */}
        <div className="p-6">
          <h1 className="text-2xl font-bold mb-4">쇼핑몰 관리 시스템</h1>
          <p>여기에 메인 콘텐츠가 들어갑니다...</p>
        </div>
      </div>
    </div>
  );
}

export default Header;
