import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

function ProductEdit() {

  const { id } = useParams();
  const [productData, setProductData] = useState({
    name: "",
    discription: "",
    price: "",
    stock: "",
    uploadImage: null,
    categoryId: "",
  });

  const [categories, setCategories] = useState([]);

  const [previewImage, setPreviewImage] = useState(null);

  useEffect(() => {
      const fetchData = async () => {
        try{
          const [resProduct, resCategories] = await Promise.all([
            axios.get(`http://localhost:8080/api/products/${id}`),
            axios.get(`http://localhost:8080/api/categories`)
          ]);
          setCategories(resCategories.data);
          setProductData(resProduct.data);
        } catch(error) {
          console.error('데이터 불러오기 실패', error);
        }
      };

      fetchData();

  }, [id])

  const modProc = async() => {
    try{
        if(window.confirm("상품을 등록하시겠습니까?")){

            const param = new FormData();
            param.append("name", productData.name);
            param.append("description", productData.description);
            param.append("price", productData.price);
            param.append("stock", productData.stock);
            param.append("categoryId", productData.categoryId);

            if (productData.uploadImage) {
              param.append("uploadImage", productData.uploadImage);
            }

            const response = await axios.post(
              "http://localhost:8080/product/regProduct", 
               param, 
               {
                 headers: {
                  "Content-Type": "multipart/form-data",
                 }
               }
            );

            if (response.status === 200) {
                alert("등록 되었습니다.");
                window.location.href = '/product-management';
            }
        }
    } catch (error) {
        alert("등록에 실패하였습니다.");
    }
  }

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setProductData({
      ...productData,
      [name]: files ? files[0] : value,
    });
  };

  return (
    <div className="p-6">
      <div className="text-2xl font-bold mb-6">상품수정</div>

        <table className="min-w-full border-collapse border border-gray-200 rounded-md">
          <tbody>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">상품명</td>
              <td className="px-4 py-3">
                <input
                  name="name"
                  value={productData.name}
                  onChange={handleChange}
                  className="border w-full px-2 py-1"
                  required
                />
              </td>
            </tr>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">상품설명</td>
              <td className="px-4 py-3">
                <textarea
                  name="description"
                  value={productData.description}
                  onChange={handleChange}
                  className="border w-full px-2 py-1"
                  required
                ></textarea>
              </td>
            </tr>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">가격</td>
              <td className="px-4 py-3">
                <input
                  name="price"
                  type="number"
                  value={productData.price}
                  onChange={handleChange}
                  className="border w-full px-2 py-1"
                  required
                />
              </td>
            </tr>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">분류명</td>
              <td className="px-4 py-3">
                <select
                  name="categoryId"
                  value={productData.categoryId}
                  onChange={handleChange}
                  className="border px-2 py-1"
                  required
                >
                  <option value="">분류 선택</option>
                    {categories.map((category) => (
                        <option key={category.id} value={category.id}>
                            {category.name}
                        </option>
                    ))}
                </select>
              </td>
            </tr>
            <tr className="border-b">
              <td className="px-4 py-3 bg-gray-100">재고</td>
              <td className="px-4 py-3">
                <input
                  name="stock"
                  type="number"
                  value={productData.stock}
                  onChange={handleChange}
                  className="border w-full px-2 py-1"
                  required
                />
              </td>
            </tr>
            <tr>
              <td className="px-4 py-3 bg-gray-100">이미지 업로드</td>
              <td className="px-4 py-3">
                {/* 기존 등록된 이미지 표시 */}
                {productData.imagePath && (
                  <div className="mb-4">
                    <div className="text-sm font-semibold mb-1">기존 이미지</div>
                    <div className="flex items-center space-x-2">
                      <img
                        src={`http://localhost:8080${productData.imagePath}`}
                        alt={productData.name}
                        className="w-16 h-16 object-cover rounded"
                      />
                      <span className="text-sm text-gray-600">
                        {extractFileName(productData.imagePath)}
                      </span>
                    </div>
                  </div>
                )}

                <div>
                  {productData.imagePath && (<div className="text-sm font-semibold mb-1">새 이미지 업로드</div>)}
                  <input
                    name="uploadImage"
                    type="file"
                    onChange={handleChange}
                    className="w-full"
                  />
                </div>
              </td>
            </tr>
          </tbody>
        </table>

        <div className="flex justify-end gap-2 p-4">
          <button type="button" onClick={modProc} className="bg-gray-200 px-4 py-2 rounded">수정</button>
          <Link to='/product-management' className="bg-gray-200 px-4 py-2 rounded">취소</Link>
        </div>
    </div>
  );
}

function extractFileName(path) {
  const fileName = path.split('/').pop();
  return fileName.split('_').pop();
}

export default ProductEdit;
