import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

function UserInfo() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/api/user")
      .then(response => {
        setUsers(response.data);
      })
      .catch(error => {
        console.error("회원정보를 불러오지 못하였습니다.", error);
      });
  }, []);

  const delUser = async () => {
    alert("삭제")
  }

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <div className="text-2xl font-bold">회원정보</div>
      </div>

      <div className="overflow-x-auto">
        <table className="min-w-full border border-gray-200">
          <thead className="bg-gray-100">
            <tr>
              <th className="px-4 py-2 border">이메일</th>
              <th className="px-4 py-2 border">이름</th>
              <th className="px-4 py-2 border">주소</th>
              <th className="px-4 py-2 border">전화번호</th>
              <th className="px-4 py-2 border">관리</th>
            </tr>
          </thead>
          <tbody>
            {users.length > 0 ? (
              users.map((user) => (
                <tr key={user.id}>
                  <td className="px-4 py-2 border">{user.email}</td>
                  <td className="px-4 py-2 border">{user.name}</td>
                  <td className="px-4 py-2 border">{user.address}</td>
                  <td className="px-4 py-2 border">{user.phone}</td>
                  <td className="px-4 py-2 border text-center">
                    <Link to={`/user-edit/${user.id}`} className="inline-flex items-center justify-center bg-blue-500 text-white mr-2 px-4 py-2 rounded no-underline">수정</Link>
                    <button onClick={() => delUser(user.id)} className="inline-flex items-center justify-center bg-blue-500 text-white px-4 py-2 rounded">삭제</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" className="text-center py-8 text-gray-500">
                  No data available
                </td>
              </tr>
            )}
          </tbody>
        </table>

        <div className="flex justify-end mt-4 items-center gap-2 text-sm text-gray-700">
          <span>Rows per page:</span>
          <select className="border rounded px-2 py-1">
            <option>10</option>
            <option>20</option>
            <option>30</option>
          </select>
          <span>-</span>
          <button className="px-2">&lt;</button>
          <button className="px-2">&gt;</button>
        </div>
      </div>
    </div>
  );
}

export default UserInfo;
