import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../api/apiClient";

function ProfileEdit() {
    const navigate = useNavigate();

    const [userData, setUserData] = useState({
        email: "",
        name: "",
        birth: "",
        phone: "",
        address: "",
    });

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await api.get("/user/profile");
                if (response.status === 200) {
                    setUserData(response.data);
                } else {
                    alert("회원 정보를 불러오지 못했습니다.");
                }
            } catch (error) {
                alert("회원 정보를 불러오는 중 오류가 발생했습니다.");
            }
        };

        fetchProfile();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;

        setUserData((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validate()) return;

        try {
            const response = await api.put("/user/profile", userData);
            if (response.status === 200) {
                alert("회원 정보가 수정되었습니다.");
                navigate("/mypage");
            } else {
                alert("수정에 실패했습니다.");
            }
        } catch (error) {
            alert("수정 중 오류가 발생했습니다.");
        }
    };

    const validate = () => {
        const { email, name, birth, phone, address } = userData;

        if (!email.includes("@")) {
            alert("이메일을 정확히 입력해 주세요.");
            return false;
        }

        if (name.trim() === "") {
            alert("성명을 입력해주세요.");
            return false;
        }

        if (!/^\d{6}$/.test(birth)) {
            alert("생년월일은 6자리 숫자로 입력해주세요.");
            return false;
        }

        if (phone.trim() === "") {
            alert("전화번호를 입력해주세요.");
            return false;
        }

        if (address.trim() === "") {
            alert("주소를 입력해주세요.");
            return false;
        }

        return true;
    };

    return (
        <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50">
            <h2 className="text-2xl font-bold text-gray-700 mb-8">회원 정보 수정</h2>

            <div className="w-full max-w-md bg-white p-8 rounded-2xl shadow-lg">
                <form className="space-y-4" onSubmit={handleSubmit}>
                    <input
                        name="email"
                        value={userData.email}
                        placeholder="이메일"
                        className="w-full px-4 py-2 border rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
                        onChange={handleChange}
                    />
                    <input
                        name="name"
                        value={userData.name}
                        placeholder="성명"
                        className="w-full px-4 py-2 border rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
                        onChange={handleChange}
                    />
                    <input
                        name="birth"
                        value={userData.birth}
                        placeholder="생년월일 (예: 920201)"
                        maxLength={6}
                        className="w-full px-4 py-2 border rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
                        onChange={(e) => {
                            const onlyNums = e.target.value.replace(/[^0-9]/g, "");
                            setUserData((prev) => ({ ...prev, birth: onlyNums }));
                        }}
                    />
                    <input
                        name="phone"
                        value={userData.phone}
                        placeholder="전화번호 (예: 01012345678)"
                        maxLength={11}
                        type="tel"
                        className="w-full px-4 py-2 border rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
                        onChange={(e) => {
                            const onlyNums = e.target.value.replace(/[^0-9]/g, "");
                            setUserData((prev) => ({ ...prev, phone: onlyNums }));
                        }}
                    />
                    <input
                        name="address"
                        value={userData.address}
                        placeholder="주소"
                        className="w-full px-4 py-2 border rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
                        onChange={handleChange}
                    />

                    <button
                        type="submit"
                        className="mt-6 w-full bg-blue-500 text-white py-2 rounded-xl hover:bg-blue-600 transition-colors"
                    >
                        수정 완료
                    </button>
                </form>
            </div>
        </div>
    );
}

export default ProfileEdit;
