import api from "../../api/apiClient";
import { useState, useEffect } from 'react';
import { useParams } from "react-router-dom";

function ProductDetail() {
  const { id } = useParams();
  const [quantity, setQuantity] = useState(1);
  const [product, setProduct] = useState(null);
  const price = 30900;

  useEffect(() => {
    api.get(`/products/${id}`)
      .then(response => {
        setProduct(response.data);
      }).catch(error => {
        console.log("상품조회에 실패하였습니다.", error);
      })
    console.log(id);
  }, [id]);

  const getDeliveryDate = () => {
    const date = new Date();
    date.setDate(date.getDate() + 3);
    const week = ['일', '월', '화', '수', '목', '금', '토'];
    const formatted = `${date.getMonth() + 1}/${date.getDate()}(${week[date.getDay()]})`;
    return formatted;
  };

  return (
    <div className="max-w-5xl mx-auto p-8">
      <div className="flex flex-col lg:flex-row gap-8 items-start">
        {/* 왼쪽 - 이미지 */}
        <div className="w-full lg:w-[55%]">
          <img
            src="/default-image.png"
            alt="메인 상품 이미지"
            className="w-full h-auto object-cover border rounded"
          />
        </div>

        {/* 오른쪽 - 상품 정보 */}
        <div className="w-full lg:w-[45%] flex flex-col gap-6 text-base lg:text-lg">
          <div className="text-gray-500 text-sm">분류1</div>

          <h1 className="text-3xl font-bold text-gray-900">상품명 (예정)</h1>

          <div className="text-2xl font-bold text-blue-600">
            {price.toLocaleString()}원
          </div>

          <div className="text-gray-600">
            무료배송 · <span className="font-semibold">{getDeliveryDate()}</span> 도착 예정
          </div>

          {/* 수량 선택 */}
          <div>
            <label className="block text-gray-700 mb-1 font-medium">수량</label>
            <div className="flex items-center border rounded w-36 overflow-hidden">
              <button
                type="button"
                onClick={() => setQuantity(prev => Math.max(1, prev - 1))}
                className="w-10 h-10 flex justify-center items-center text-xl border-r hover:bg-gray-100"
              >
                –
              </button>
              <input
                type="number"
                value={quantity}
                onChange={(e) => {
                  const value = Math.max(1, parseInt(e.target.value) || 1);
                  setQuantity(value);
                }}
                className="w-full text-center outline-none"
                min="1"
              />
              <button
                type="button"
                onClick={() => setQuantity(prev => prev + 1)}
                className="w-10 h-10 flex justify-center items-center text-xl border-l hover:bg-gray-100"
              >
                +
              </button>
            </div>
          </div>

          {/* 총 결제 금액 */}
          <div className="text-right font-semibold text-xl text-gray-800">
            총 결제금액: {(price * quantity).toLocaleString()}원
          </div>

          {/* 구매 버튼 */}
          <div className="flex justify-center">
            <button className="w-48 bg-gray-800 hover:bg-gray-900 text-white py-3 rounded text-lg">
              바로구매
            </button>
          </div>
        </div>
      </div>

        {/* 구분선 영역 - 상품 설명 */}
      <div className="mt-12 border-t pt-8">
        <h2 className="text-xl font-semibold mb-4">상품 설명</h2>
        <p className="text-gray-700 leading-relaxed">
            상품설명 (예정)
        </p>
      </div>

      {/* 구분선 영역 - 배송 유의사항 */}
      <div className="mt-12 border-t pt-8">
        <h2 className="text-xl font-semibold mb-4">배송 및 유의사항</h2>
        <ul className="text-gray-700 list-disc list-inside space-y-2">
            <li>오후 3시 이전 결제 시 당일 출고됩니다. (공휴일 제외)</li>
            <li>제주/도서산간 지역은 추가 배송비가 발생할 수 있습니다.</li>
            <li>상품 수령 후 7일 이내 교환/반품 신청이 가능합니다.</li>
            <li>위생 상품 특성상 개봉 후 단순 변심에 의한 반품은 불가합니다.</li>
        </ul>
      </div>
    </div>
  );
}

export default ProductDetail;
